// src/components/auth/Register.jsx
import { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { showAlert } from "../../slices/alertSlice";
import { registerUser, clearError } from "../../slices/authSlice";

function Register() {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [isSubmitting, setIsSubmitting] = useState(false);
  const { loading, error, isAuthenticated } = useSelector(state => state.auth);
  
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    password2: ''
  });

  const { name, email, password, password2 } = formData;

  // Handle errors
  useEffect(() => {
    if (error) {
      if (error.errors && error.errors.length > 0) {
        error.errors.forEach((err) => 
          dispatch(showAlert(err.msg || err.message, 'danger'))
        );
      } else if (error.message) {
        dispatch(showAlert(error.message, 'danger'));
      }
      dispatch(clearError());
    }
  }, [error, dispatch]);

  // Redirect after successful registration
  useEffect(() => {
    if (isAuthenticated) {
      dispatch(showAlert('Registration successful! Welcome!', 'success'));
      navigate('/dashboard'); // or wherever you want to redirect
    }
  }, [isAuthenticated, navigate, dispatch]);

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  // Basic email validation
  const isValidEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    
    // Prevent double submission
    if (isSubmitting) return;
    
    // Client-side validation
    if (!name.trim()) {
      dispatch(showAlert('Name is required', 'danger'));
      return;
    }
    
    if (!isValidEmail(email)) {
      dispatch(showAlert('Please enter a valid email address', 'danger'));
      return;
    }
    
    if (password.length < 6) {
      dispatch(showAlert('Password must be at least 6 characters', 'danger'));
      return;
    }
    
    if (password !== password2) {
      dispatch(showAlert('Passwords do not match', 'danger'));
      return;
    }

    // Set loading state and dispatch register action
    setIsSubmitting(true);
    try {
      await dispatch(registerUser({ 
        name: name.trim(), 
        email: email.toLowerCase(), 
        password 
      })).unwrap();
      // Success is handled by the useEffect above
    } catch (err) {
      // Error is handled by the useEffect above
      console.error('Registration failed:', err);
    } finally {
      setIsSubmitting(false);
    }
  };

  // Prevent access if already authenticated
  if (isAuthenticated) {
    return null; // or a loading spinner
  }

  // Use isSubmitting instead of loading for form state
  const isFormDisabled = isSubmitting || loading;

  return (
    <>
      <h1 className="large text-primary">Sign Up</h1>
      <p className="lead">
        <i className="fas fa-user"></i> Create Your Account
      </p>
      <form className="form" onSubmit={onSubmit}>
        <div className="form-group">
          <input
            type="text"
            placeholder="Name"
            name="name"
            value={name}
            onChange={onChange}
            required
            disabled={isFormDisabled}
          />
        </div>
        <div className="form-group">
          <input
            type="email"
            placeholder="Email Address"
            name="email"
            value={email}
            onChange={onChange}
            required
            disabled={isFormDisabled}
          />
          <small className="form-text">
            This site uses Gravatar so if you want a profile image, use a
            Gravatar email
          </small>
        </div>
        <div className="form-group">
          <input
            type="password"
            placeholder="Password"
            name="password"
            value={password}
            onChange={onChange}
            minLength="6"
            required
            disabled={isFormDisabled}
          />
        </div>
        <div className="form-group">
          <input
            type="password"
            placeholder="Confirm Password"
            name="password2"
            value={password2}
            onChange={onChange}
            minLength="6"
            required
            disabled={isFormDisabled}
          />
        </div>
        <input 
          type="submit" 
          className="btn btn-primary" 
          value={isSubmitting ? "Creating Account..." : "Register"}
          disabled={isFormDisabled}
        />
      </form>
      <p className="my-1">
        Already have an account? <Link to="/login">Sign In</Link>
      </p>
    </>
  );
}

export default Register;
